const getEtagElement = target => {
  return Object.values(target.elements).find(element => element['@odata.etag'])
}

const isSingleton = target => {
  return target['@odata.singleton'] || (target['@odata.singleton.nullable'] && target['@odata.singleton'] !== false)
}

module.exports = {
  getEtagElement,
  isSingleton
}
