const UNAUTHORIZED = { code: '401', message: 'Unauthorized' }
const FORBIDDEN = { code: '403', message: 'Forbidden' }

const getRequiresAsArray = definition => {
  if (!definition['@requires']) {
    return []
  }

  return Array.isArray(definition['@requires']) ? definition['@requires'] : [definition['@requires']]
}

module.exports = {
  UNAUTHORIZED,
  FORBIDDEN,
  getRequiresAsArray
}
